import React, { Component, useState, useEffect } from "react";
import "./SignIn.css";
// import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
// import { faEye, faEyeSlash } from "@fortawesome/free-solid-svg-icons";
import image from "../../asset/img.png";
import indlogo from "../../asset/Group.png";
import {
  Link,
  MemoryRouter as Router,
  Redirect,
  Route,
  Switch,
} from "react-router-dom";

import { useDispatch } from "react-redux";
import { login } from "../../features/loginAuth/loginAuthSlice.js";
import { Formik, Form, Field, ErrorMessage } from "formik";
import { useFormik } from "formik";
import * as Yup from "yup";

// const eye = <FontAwesomeIcon icon={faEye} />;
// const eyeslash = <FontAwesomeIcon icon={faEyeSlash} />;

const paperStyle1 = {
  padding: 20,
  height: "60",
  width: 450,
  margin: "70px auto",
};

const SignIn = () => {
  const dispatch = useDispatch();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [state, setState] = useState({
    mobile: "",

    token: "",
    id: null,
    validEmail: false,
    isEmptyEmail: false,
    validPassword: false,
    type: "password",
    types: "password",
    showElement: true,
    result: false,
    name: "",
    session_id: null,
    userRole: "",
  });

  //   const initialValues = {
  //     emal: '',
  //     passwod: '',
  //     remember: false
  // }
  // const validationSchema = Yup.object().shape({
  //   email: Yup.string()
  //     .email("Invalid email address format")
  //     .required("Email is required"),
  //   password: Yup.string()
  //   .min(6, "Password must be atleast 6 characters")
  //     .required("Password is required")
  // });

  useEffect(() => {
    window.scrollTo({
      top: 0,
      behavior: "instant",
    });
  }, []);

  const formik = useFormik({
    initialValues: {
      email: "",
      password: "",
    },
    onSubmit: async (values) => {
      console.log(values);

      var data = {
        email: values.email,
        password: values.password,
      };
      const myJSON = JSON.stringify(data);
      console.log(myJSON, "mjson");
      window.go.main.App.MyLogin(myJSON).then((result) => {
        const userObj = JSON.parse(result);

        var data = [];
        data.push(userObj);
        if (data.length > 0) {
          setState((ps) => {
            return {
              ...ps,
              result: true,
              name: userObj.user_Name,
              id: userObj.id,
              token: userObj.token,
              userRole: userObj.role,
            };
          });
          // console.log(userObj.role, "role in sign");
          dispatch(
            login({
              name: userObj.user_Name,
              id: userObj.id,
              token: userObj.token,
              userRole: userObj.role,
              backClick: true,
            })
          );
        } else {
          alert("login failed");
        }
      });
    },
    validationSchema: Yup.object().shape({
      email: Yup.string()
        .email("Enter a valid email")
        .required("Enter a  email "),
      password: Yup.string()
        .required("Required")
        .min(6, "Password must be atleast 6 characters"),
    }),
  });

  // function handleSubmit(e) {
  //   e.preventDefault();

  //   const data = {
  //     email: email,
  //     password: password,
  //   };
  //   const myJSON = JSON.stringify(data);
  //   console.log(myJSON, "mjson");
  //   window.backend.myLogin(myJSON).then((result) => {
  //     const userObj = JSON.parse(result);

  //     var data = [];
  //     data.push(userObj);
  //     if (data.length > 0) {
  //       setState((ps) => {
  //         return {
  //           ...ps,
  //           result: true,
  //           name: userObj.user_Name,
  //           id: userObj.id,
  //           token: userObj.token,
  //           userRole: userObj.role,
  //         };
  //       });

  //       dispatch(
  //         login({
  //           name: userObj.user_Name,
  //           id: userObj.id,
  //           token: userObj.token,
  //           userRole: userObj.role,
  //           backClick: true,
  //         })
  //       );
  //     } else {
  //       alert("login failed");
  //     }
  //   });
  // }

  // const handleClick = () =>
  //   setState(({ type }) => ({
  //     type: type === "text" ? "password" : "text",
  //   }));

  // const pwdhandleClick = () =>
  //   setState(({ types }) => ({
  //     types: types === "text" ? "password" : "text",
  //   }));

  if (state.result) {
    return (
      <Redirect
        to={{
          pathname: "/select-page",
          state: { name: state.name },
        }}
      />
    );
  }
  return (
    <>
      <div className="log">
        <div>
          <img
            src={indlogo}
            alt=""
            style={{ float: "left", width: "15%", height: "15%" }}
          />
          <h2
            style={{
              float: "right",
              marginRight: "10%",
              fontSize: "25px",
              marginTop: "1.5%",
              marginLeft: "0.7%",
            }}
          >
            GameOn
          </h2>
          <img
            src={indlogo}
            alt=" "
            style={{ width: "5%", height: "5%", float: "right" }}
          />
        </div>
        <div className="box-container">
          <div className="box">
            <div className="left-side">
              <h5 className="left-top">GameOn</h5>
              <img src={image} alt="" className="login_image" />
              <h5 className="left_bottom"> Smart way to test Game App </h5>

              {/* {state.result && <Link to="/select-page"></Link>} */}
            </div>

            <div className="right-side">
              <div className="contents">
                {/* 
            <form id="loginform" onSubmit={handleSubmit}>
                  <div className="form-group">
                    <label>Email address</label>
                    <input
                      type="email"
                      className="form-control"
                      id="EmailInput"
                      name="EmailInput"
                      value={state.email}
                      aria-describedby="emailHelp"
                      placeholder="Enter email"
                      onChange={(e) => {
                        setEmail(e.target.value);
                      }}
                    />
                  </div>
                  <div className="form-group">
                    <label>Password</label>
                    <input
                      type="password"
                      className="form-control"
                      value={state.password}
                      id="exampleInputPassword1"
                      placeholder="Password"
                      onChange={(e) => {
                        setPassword(e.target.value);
                      }}
                    />
                  </div>
                  <div className="form-group form-check">
                    <input
                      type="checkbox"
                      className="form-check-input"
                      id="exampleCheck1"
                    />
                    <label className="form-check-label">Check me out</label>
                  </div>
                  <button
                    type="submit"
                    style={{
                      marginTop: "8%",
                      marginLeft: "8%",
                      background: "#278EF1",
                      borderRadius: "20px",
                      width: "84%",
                      height: "5%",
                      padding: "3px",
                      color: "white",
                    }}
                  >
                    Submit
                  </button>
                </form> */}

                <Formik
                  initialValues={formik.initialValues}
                  validationSchema={formik.validationSchema}
                >
                  {({ errors, touched }) => (
                    <Form onSubmit={formik.handleSubmit}>
                      <div className="form-group">
                        <label htmlFor="email">Email</label>
                        <input
                          type="email"
                          name="email"
                          className={`form-control ${
                            formik.errors.email ? "is-invalid" : ""
                          }`}
                          placeholder="Enter email"
                          onChange={formik.handleChange}
                          value={formik.values.email}
                        />
                        <div className="invalid-feedback">
                          {formik.errors.email}
                        </div>
                      </div>

                      <div className="form-group">
                        <label htmlFor="password">Password</label>
                        <input
                          type="password"
                          name="password"
                          className={`form-control ${
                            formik.errors.password ? "is-invalid" : ""
                          }`}
                          placeholder="Enter email"
                          onChange={formik.handleChange}
                          value={formik.values.password}
                        />
                        <div className="invalid-feedback">
                          {formik.errors.password}
                        </div>
                      </div>
                      <button
                        type="submit"
                        style={{
                          marginTop: "8%",
                          marginLeft: "8%",
                          background: "#278EF1",
                          borderRadius: "20px",
                          width: "84%",
                          height: "5%",
                          padding: "3px",
                          color: "white",
                        }}
                      >
                        Submit
                      </button>
                    </Form>
                  )}
                </Formik>
              </div>
            </div>

            <br></br>
          </div>
        </div>
      </div>
    </>
  );
};

